import React, {Fragment} from 'react';

const CheckRadio = ({label, name, value, currentValue, type, onChange}) => {
    return (
        <Fragment>
            <div className={"form-group"}>
                <label htmlFor={"#" + name}>{label || ""}</label>
                <input type={type || "checkbox"} onChange={e => {
                    console.log(e)
                }}
                       id={name} checked={value === currentValue} data-switch="bool"/>
                <label htmlFor={"#" + name} data-on-label="On" data-off-label="Off"/>
            </div>
        </Fragment>
    );
};

export default CheckRadio;
