import React from "react";
import Joi from "joi-browser";
import Form from "../common/form";
import {faArrowRight} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

class RegisterDetails extends Form {
    state = {
        data: {firstname: "", lastname: "", email: "", contact: ""},
        errors: {},
        isSecond: false
    };

    schema = {
        firstname: Joi.string()
            .required()
            .label("First Name"),
        lastname: Joi.string()
            .required()
            .label("Lastname"),
        email: Joi.string()
            .required()
            .email()
            .label("Email"),
        contact: Joi.string()
            .required()
            .label("Contact"),
        residence: Joi.string()
            .required()
            .label("Residence"),
        nationality: Joi.string()
            .required()
            .label("Nationality"),
        dob: Joi.string()
            .required()
            .label("Date o Birth"),
        gender: Joi.string()
            .required()
            .label("Gender"),
    };

    doSubmit = async () => {
        try {
        } catch (ex) {
            if (ex.response && ex.response.status === 400) {
                const errors = {...this.state.errors};
                errors.username = ex.response.data;
                this.setState({errors});
            }
        }
    };

    render() {

        const {isSecond} = this.state;

        return (
            <div>
                <h1 className={"my-3 text-dark fw-light"}>Register</h1>
                <form onSubmit={this.handleSubmit}>
                    {this.renderInput("firstname", "Firstname")}
                    {this.renderInput("lastname", "Lastname")}
                    {this.renderInput("email", "Email")}
                    {this.renderInput("contact", "Contact", "number")}

                    <div className={"collapse fade"} id={"moreFields"}>
                        {this.renderInput("dob", "DOB", "datetime")}
                        {this.renderInput("residence", "Residence", "text")}
                        {this.renderInput("nationality", "Nationality", "text")}
                        <div className={"row"}>
                            <div className={"col-6"}>
                                <input id={"fileInput"} type={"file"} name={"file"} hidden/>
                                <label className={"my-md-2 btn btn-outline-primary btn-lg px-3 py-2 w-100"}
                                       htmlFor={"#fileInput"}>
                                    Upload
                                </label>
                            </div>
                        </div>
                    </div>

                    <button data-bs-toggle={"collapse"} data-bs-target={"#moreFields"} type="button"
                            className="btn btn-outline-warning btn-lg">
                        Continue <FontAwesomeIcon icon={faArrowRight} className={"mx-2"}/>
                    </button>
                </form>
            </div>
        );
    }
}

export default RegisterDetails;
