import React, {Fragment} from 'react';

const CheckRadio = ({label, name, value, currentValue, type, onChange}) => {
    return (
        <Fragment>
            <label htmlFor={"#" + name}>{label || ""}</label>
            <input type={type || "checkbox"} id={name} checked={value === currentValue} data-switch="bool"/>
            <label htmlFor={"#" + name} data-on-label="On" data-off-label="Off"/>

        </Fragment>
    );
};

export default CheckRadio;
