import React from "react";
import Joi from "joi-browser";
import Form from "../common/form";
import {faArrowRight} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import FileInput from "../common/fileinput";
import CheckRadio from "../common/checkRadio";
import {studentApi} from "../../config.json";
import httpService from "../../services/httpService";

class RegisterDetails extends Form {
    state = {
        data: {firstname: "", lastname: "", email: "", contact: ""},
        radios: {
            gender: ""
        },
        errors: {},
        isSecond: false,
        files: {
            id: null,

        }
    };

    schema = {
        firstname: Joi.string()
            .required()
            .label("First Name"),
        lastname: Joi.string()
            .required()
            .label("Lastname"),
        email: Joi.string()
            .required()
            .email()
            .label("Email"),
        contact: Joi.string()
            .required()
            .label("Contact"),
        residence: Joi.string()
            .required()
            .label("Residence"),
        nationality: Joi.string()
            .required()
            .label("Nationality"),
        dob: Joi.string()
            .required()
            .label("Date o Birth"),
        gender: Joi.string()
            .required()
            .label("Gender"),
        password: Joi.string()
            .required()
            .min(6)
            .label("Password")
    };

    doSubmit = async () => {
        try {

        let {data} = httpService.post(studentApi,formData);

        } catch (ex) {
            if (ex.response && ex.response.status === 400) {
                const errors = {...this.state.errors};
                errors.username = ex.response.data;
                this.setState({errors});
            }
        }
    };

    checkChange = (name, value) => {
        const state = {...this.state};
        state.radios[name] = value;
        this.setState(state);
    }

    render() {

        const {isSecond, radios} = this.state;

        return (
            <div>
                <h1 className={"my-3 text-dark fw-light"}>Register</h1>
                <form onSubmit={this.handleSubmit}>
                    {this.renderInput("firstname", "Firstname")}
                    {this.renderInput("lastname", "Lastname")}
                    {this.renderInput("email", "Email")}
                    {this.renderInput("contact", "Contact", "number")}

                    <label>Gender</label>
                    <div className={"d-flex justify-content-around align-items-center mb-3"}>
                        <CheckRadio label={"Male"} currentValue={radios.gender} name={"gender"} type={"radio"}
                                    value={"m"} onChange={value => this.checkChange("gender", value)}/>
                        <CheckRadio label={"Female"} currentValue={radios.gender} name={"gender"} type={"radio"}
                                    value={"f"} onChange={value => this.checkChange("gender", value)}/>
                    </div>

                    <div className={"collapse fade"} id={"moreFields"}>
                        {this.renderInput("dob", "DOB", "date")}
                        {this.renderInput("residence", "Residence", "text")}
                        {this.renderInput("nationality", "Nationality", "text")}
                        {this.renderInput("password", "Password", "text")}
                        <div className={"row mb-3"}>
                            <div className={"col-6"}>
                                <FileInput label={"Upload Identification document"}/>
                            </div>
                        </div>
                    </div>

                    <button data-bs-toggle={"collapse"} data-bs-target={"#moreFields"} type="button"
                            className="btn btn-outline-warning btn-lg">
                        Continue <FontAwesomeIcon icon={faArrowRight} className={"mx-2"}/>
                    </button>
                </form>
            </div>
        );
    }
}

export default RegisterDetails;
