import React, {useEffect} from 'react';
import {Provider} from 'react-redux';
import store from './store';
import {HashRouter, Route, Switch} from "react-router-dom";
import $ from "jquery";

import RegisterWizard from "./components/register/RegisterWizard";

import './App.css';


const App = () => {

    useEffect(() => {
        return () => {
            removePreloader();
        };
    }, []);


    return (
        <Provider store={store}>
            <HashRouter>
                <Switch>
                    <Route path={"/register"} render={props => <RegisterWizard {...props}/>}/>
                    <Route path={"/"} render={props => <RegisterWizard {...props}/>}/>
                </Switch>
            </HashRouter>
        </Provider>
    );
};

export const showPreloader = () => {
    $("#preloader").fadeIn();
    $("body").removeClass("loading").addClass("show");
}

export const removePreloader = () => {
    $("#preloader").fadeOut();
    $("body").addClass("loading").removeClass("show");
}

export default App;
